import{o as l,c as i,a as e,b as a,e as r,d as t}from"./app.9d0c7254.js";const h='{"title":"Learning how to learn part3","description":"","frontmatter":{"title":"Learning how to learn part3","date":"2018-05-08T18:24:04.000Z","tags":["Summary"]},"headers":[{"level":2,"title":"前言","slug":"前言"},{"level":2,"title":"漫话拖延症","slug":"漫话拖延症"},{"level":3,"title":"习惯有几个步骤","slug":"习惯有几个步骤"},{"level":3,"title":"解决方案","slug":"解决方案"},{"level":3,"title":"思想上的转变","slug":"思想上的转变"},{"level":2,"title":"大脑的记忆相关","slug":"大脑的记忆相关"}],"relativePath":"blog/Learning-how-to-learn-part3.md","lastUpdated":1640015528977}',d={},n=a("h2",{id:"前言"},[a("a",{class:"header-anchor",href:"#前言","aria-hidden":"true"},"#"),r(" 前言")],-1),s=a("blockquote",null,[a("p",null,"第三篇，保持一定速度更新，会有更多的新东西的~")],-1),u=t('<h2 id="漫话拖延症"><a class="header-anchor" href="#漫话拖延症" aria-hidden="true">#</a> 漫话拖延症</h2><ul><li>拖延症的产生源于习惯</li></ul><h3 id="习惯有几个步骤"><a class="header-anchor" href="#习惯有几个步骤" aria-hidden="true">#</a> 习惯有几个步骤</h3><ul><li>触发信息</li><li>行为过程</li><li>奖励</li><li>信念</li></ul><h3 id="解决方案"><a class="header-anchor" href="#解决方案" aria-hidden="true">#</a> 解决方案</h3><ul><li>坚持写计划日志，追踪何时完成目标，检查计划是否有效</li><li>每天给自己规定一些日常和任务（每天睡前规划第二天的，目标渗透到意识中确保执行，把任务分解成小的，奖励机制的执行短暂的休息pm5以后）</li><li>留意拖延信号，控制干扰信号的减少</li><li>对新体系的信赖</li><li>拖延症发生制定备用计划</li><li>每天先处理最棘手的事情</li></ul><h3 id="思想上的转变"><a class="header-anchor" href="#思想上的转变" aria-hidden="true">#</a> 思想上的转变</h3><ul><li>把注意力放在过程上而不是结果上</li><li>不要给自己找借口</li></ul><h2 id="大脑的记忆相关"><a class="header-anchor" href="#大脑的记忆相关" aria-hidden="true">#</a> 大脑的记忆相关</h2><ul><li>大脑记忆系统（位置）（东西外观）</li><li>利用视觉卡片记忆，画面有趣，联想记忆</li><li>手写编码记忆</li><li>记忆人，间隔长时间回忆</li><li>长期的记忆需要间隔的回忆和练习</li><li>星型胶质细胞照顾神经元</li><li>记忆宫殿是有用的，通过一些场景的想象来记忆一些联系不大的概念</li><li>记忆工具有利于构建组块和大局观</li><li>多实用这些技巧，熟能生巧</li><li>高效的视觉和空间记忆能力</li><li>难忘的画面去记忆一个要点，看，听，嗅</li><li>创造简单的记忆群，数字到概念</li><li>通过记忆消耗资料</li></ul>',10);d.render=function(a,r,t,h,d,o){return l(),i("div",null,[n,s,e(" more "),u])};export default d;export{h as __pageData};
